```typescript
import * as pulumi from "@pulumi/pulumi";
import * as proxmoxve from "@muhlba91/pulumi-proxmoxve";

const exampleRepository = new proxmoxve.apt.standard.Repository("exampleRepository", {
    handle: "no-subscription",
    node: "pve",
});
